
public class intercambioValores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int a= 5, b= 8;
			
			int tmp;
			
			tmp=a;
			
			a=b;
			
			b=tmp;
			System.out.println("El valor de a ahora es: "+a);
			System.out.println("El valor de b ahora es: "+b);
			
			}
	}

